import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './Coin.css';

const Coin = () => {
  const params = useParams();
  const [coin, setCoin] = useState({});

  const url = `https://api.coingecko.com/api/v3/coins/${params.coinId}`;

  useEffect(() => {
    axios.get(url)
      .then((res) => {
        setCoin(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className='coin-container'>
      <div className='content'>
        <h1>{coin.name}</h1>
      </div>
      <div className='rank'>
        <span className='rank-btn'>Rank # {coin.market_cap_rank}</span>
      </div>
      <div className='info'>
        <div className='coin-heading'>
          {coin.image ? <img src={coin.image.small} alt='' /> : null}
          <p>{coin.name}</p>
          <p>{coin.symbol}</p>
        </div>
        <div className='coin-price'>
          <h1>{coin.market_data.current_price}</h1>
        </div>
      </div>
      <div className='content'>
        <table>
          <thead>
            <tr>
              <th>1h</th>
              <th>24h</th>
              <th>7d</th>
              <th>14d</th>
              <th>30d</th>
              <th>1yr</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{coin.market_data.price_change_percentage_1h_in_currency.eur}</td>
              <td>{coin.market_data.price_change_percentage_24h_in_currency.eur}</td>
              <td>{coin.market_data.price_change_percentage_7d_in_currency.eur}</td>
              <td>{coin.market_data.price_change_percentage_14d_in_currency.eur}</td>
              <td>{coin.market_data.price_change_percentage_30d_in_currency.eur}</td>
              <td>{coin.market_data.price_change_percentage_1yr_in_currency.eur}</td>
            </tr>
          </tbody>
        </table>
      </div>
      <div className='content'>
        <div className='stats'>
          <div className='left'></div>
          <div className='right'></div>
        </div>
      </div>
    </div>
  );
};

export default Coin;
